sp_widget.script:
(function () {
	if(input) {
		if(input.action == "send_quiz") {
			new NewJoinerUtils().sendQuizAgain(input.metric_type_sys_id, input.user_sys_id);
		}
		return;
	}
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	var quiz_sys_id = $sp.getParameter('sys_id');
	data.is_admin = gs.getUser().hasRole('x_487112_newjoiner.admin');
	console.log(data.is_admin);

	data.metric_type = {};

	var quiz_gr = new GlideRecord('asmt_assessment_instance');
	if (!quiz_gr.get(quiz_sys_id)) {
		gs.addErrorMessage('Please provide a valid Quiz ID');
		data.quiz.valid = false;
		return;
	}

	var new_joiner_utils = new NewJoinerUtils();

	var	metric_instance_details = new_joiner_utils.getMetricInstanceDetails(quiz_sys_id);
	var metric_type_details = new_joiner_utils.getMetricTypeDetails(quiz_gr.getValue('metric_type'));
	var quiz_instance_details = new_joiner_utils.getQuizScore(quiz_sys_id);

	data.quiz = {
		valid: true,
		// Metric Type Details
		metric_type: quiz_gr.getValue('metric_type'),
		metric_name: metric_type_details.name,
		// Quiz Instance
		user: quiz_instance_details.user,
		score: quiz_instance_details.score,
		categories: metric_instance_details.categories,
	};
	data.items = [
		{
			name: 'Questions',
			count: metric_type_details.questions,
		},
		{
			name: 'Unanswered',
			count: quiz_instance_details.unanswered.amount,
		},
		{
			name: 'Correct',
			count: quiz_instance_details.correct.amount,
		},
		{
			name: 'Incorrect',
			count: quiz_instance_details.incorrect.amount,
		}
	];
})();
