sys_security_acl.script:
var asmt_assessment_instance_gr = new GlideAggregate('asmt_assessment_instance');
asmt_assessment_instance_gr.addQuery('metric_type.evaluation_method', 'quiz');
asmt_assessment_instance_gr.addQuery('metric_type', current.getValue('metric_type'));
asmt_assessment_instance_gr.addQuery('user', gs.getUserID());
asmt_assessment_instance_gr.addAggregate('COUNT');
asmt_assessment_instance_gr.query();

answer = (asmt_assessment_instance_gr.next() && asmt_assessment_instance_gr.getAggregate('COUNT') == 0) || gs.hasRole('x_487112_eval.admin');
